package samplejava;

public class arithimeticoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 5, b = 15;
//arithimetic operator
int sum=a+b;
int variable=a-b;
int operator=a*b;
int remained=a/b;

System.out.println("sum is :"+sum);
System.out.println("variable is :"+variable);
System.out.println("operator is :"+operator);
System.out.println("remained is :"+remained);




	}

}
