package samplejava;

public class relationaloperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a=20,b=10;
System.out.println("a==b :"+(a==b));
System.out.println("a!=b :"+(a!=b));
System.out.println("a>b :"+(a>b));
System.out.println("a<b :"+(a<b));
System.out.println("a<=b :"+(a<=b));
System.out.println("a>=b :"+(a>=b));


//bitwise operator
int c=6;
int d=7;
System.out.println("a&b :"+(a&b));
System.out.println("a|b :"+(a|b));
System.out.println("a^b :"+(a^b));
System.out.println("a<<1 :"+(a<<1));
System.out.println("a>>1 :"+(a>>1));
System.out.println("a>>>1 :"+(a>>>1));


	}

}
